import os
import sys


# GLOBALS --------------------------------------------------------------------------------------

# for short input sets, it can be declared here instead of in seperate files
# otherwise, tests go in files named AoC_xx_test_x.txt and input goes in AoC_xx_input.txt
tests = [
	"mjqjpqmgbljsphdztnvjfqwrcgsmlb",		# 7 
	"bvwbjplbgvbhsrlpgdmjqwftvncz",			# 5
	"nppdvjthqldpwncqszvftbrmjlhg", 		# 6
	"nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg",	# 10
	"zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw"		# 11
]
inp = ""
isTest = False

doTests = True
doInput = True
enablePart1 = True
enablePart2 = True
#-----------------------------------------------------------------------------------------------

# Move a rolling window over the text. Add the window into a set and check if the size of the set matches the size of the window
def find_marker(txt, size):
	for i in range(0, len(txt)-size):
		if len(set(txt[i:i+size])) == size:
			return i+size, txt[i:i+size]

def main_1(inp):
	print(find_marker(inp,4))

def main_2(inp):
	print(find_marker(inp,14))


def read_input(filename):
	with open(filename, "r") as f:
		raw = f.readlines()
	stream = map(lambda x: x.strip(" \n\t"), raw)
	return stream
	

if __name__ == "__main__":
	if doTests:
		# read tests
		if len(tests) == 0:
			i = 0
			while True:
				i += 1
				testfile = sys.argv[0].replace(".py", ("_test_%d.txt" % i))
				if os.path.isfile(testfile):
					tests.append([x for x in read_input(testfile)])
				else:
					break
		
	if doInput:
		# read input
		if len(inp) == 0:
			inp = [x for x in read_input(sys.argv[0].replace(".py", "_input.txt"))]

	if doTests:
		# run tests
		isTest = True
